import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { v4 as uuidv4 } from "uuid";
import { EditTodoForm } from "./EditTodoForm";

export const TodoWrapper = () => {
  // Initialize state with 5 fixed tasks
  const [todos, setTodos] = useState([
    { id: uuidv4(), task: "Complete the project report", completed: false, isEditing: false },
    { id: uuidv4(), task: "Read 20 pages of a book", completed: false, isEditing: false },
    { id: uuidv4(), task: "Exercise for 30 minutes", completed: false, isEditing: false },
    { id: uuidv4(), task: "Buy groceries", completed: false, isEditing: false },
    { id: uuidv4(), task: "Call the dentist", completed: false, isEditing: false }
  ]);

  const addTodo = (todo) => {
    setTodos([
      ...todos,
      { id: uuidv4(), task: todo, completed: false, isEditing: false },
    ]);
  };

  const deleteTodo = (id) => setTodos(todos.filter((todo) => todo.id !== id));

  const editTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const editTask = (task, id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo
      )
    );
  };

  // Toggle completion status when a checkbox is clicked
  const toggleCompletion = (id, isCompleted) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: isCompleted } : todo
      )
    );
  };

  return (
    <div className="TodoWrapper">
      <h1>Get Things Done!</h1>
      <TodoForm addTodo={addTodo} />
      {/* Display todos */}
      {todos.map((todo) =>
        todo.isEditing ? (
          <EditTodoForm editTodo={editTask} task={todo} key={todo.id} />
        ) : (
          <Todo
            key={todo.id}
            task={todo}
            deleteTodo={deleteTodo}
            editTodo={editTodo}
            toggleCompletion={toggleCompletion}
          />
        )
      )}
    </div>
  );
};
