import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faFloppyDisk } from '@fortawesome/free-solid-svg-icons';

export const Todo = ({ task, editTodo, toggleComplete, confirmTodo }) => {
  return (
    <div className={`Todo ${task.confirmed ? "dimmed" : ""}`}> {/* Dimming the todo if confirmed */}
      <p
        className={`${task.completed ? "completed" : "incompleted"}`}
        onClick={() => toggleComplete(task.id)}
      >
        {task.task}
      </p>
      <div className="actions">
        {/* Edit icon */}
        <FontAwesomeIcon className="edit-icon" icon={faPenToSquare} onClick={() => editTodo(task.id)} />

        {/* Save icon (confirm button), disable if already confirmed */}
        <FontAwesomeIcon 
          className="confirm-icon" 
          icon={faFloppyDisk} 
          onClick={() => confirmTodo(task.id)} 
          style={{ cursor: task.confirmed ? 'default' : 'pointer' }} 
          disabled={task.confirmed}
        />
      </div>
    </div>
  );
};
