import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { EditTodoForm } from "./EditTodoForm";
import { NoteForm } from "./NoteForm";
import Button from "./Button";

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([
    { id: 1, task: "Engine Performance", completed: false, isEditing: false, showNoteForm: false, note: "", confirmed: false },
    { id: 2, task: "Tire Condition", completed: false, isEditing: false, showNoteForm: false, note: "", confirmed: false },
    { id: 3, task: "Brake Condition", completed: false, isEditing: false, showNoteForm: false, note: "", confirmed: false },
    { id: 4, task: "Headlights & Signals", completed: false, isEditing: false, showNoteForm: false, note: "", confirmed: false },
    { id: 5, task: "Windshields & Wipers", completed: false, isEditing: false, showNoteForm: false, note: "", confirmed: false }
  ]);

  const [carName, setCarName] = useState('');
  const [isCarAdded, setIsCarAdded] = useState(false); // State to check if car was added

  const toggleComplete = (id) => {
    setTodos(
      todos.map((todo) => {
        // If marking as complete, show the note form
        if (!todo.completed && todo.id === id) {
          return { ...todo, showNoteForm: true, completed: true }; 
        }
        // If undoing the complete action, hide the note form and reset note
        if (todo.completed && todo.id === id) {
          return { ...todo, showNoteForm: false, completed: false, note: "" };
        }
        return todo;
      })
    );
  };

  const handleNoteChange = (e, id) => {
    const { value } = e.target;
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, note: value } : todo));
  };

  const handleOutsideClick = (id) => {
    const todo = todos.find((t) => t.id === id);
    if (!todo.note.trim()) {
      alert("Note is required for completed tasks.");
    } else {
      setTodos(todos.map((todo) => todo.id === id ? { ...todo, showNoteForm: false } : todo));
    }
  };

  const editTodo = (id) => {
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo));
  };

  const editTask = (task, id) => {
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo));
  };

  // Confirm Todo (save button logic)
  const confirmTodo = (id) => {
    setTodos(
      todos.map((todo) => 
        todo.id === id ? { ...todo, confirmed: true, isEditing: false } : todo
      )
    );
  };

  const handleInspectionResult = () => {
    if (carName) {
      alert(`Inspection results for ${carName} submitted!`);
    } else {
      alert('Please provide a car name.');
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Car Inspection Report</h1>
      <TodoForm setCarName={setCarName} setIsCarAdded={setIsCarAdded} />

      {isCarAdded && (
        <>
          {todos.map((todo) =>
            todo.isEditing ? (
              <EditTodoForm editTodo={editTask} task={todo} />
            ) : (
              <div key={todo.id}>
                <Todo
                  key={todo.id}
                  task={todo}
                  editTodo={editTodo}
                  toggleComplete={toggleComplete}
                  confirmTodo={confirmTodo} // Pass the confirm function to each todo
                />
                {todo.showNoteForm && (
                  <NoteForm
                    note={todo.note}
                    onNoteChange={handleNoteChange}
                    todoId={todo.id}
                    onOutsideClick={handleOutsideClick}
                  />
                )}
              </div>
            )
          )}
          <Button onClick={handleInspectionResult} className="inspection-result-btn">
            Inspection Result
          </Button>
        </>
      )}
    </div>
  );
};
