import React from 'react';

export const NoteForm = ({ note, onNoteChange, todoId, isDisabled }) => {
  return (
    <form className={`NoteForm ${isDisabled ? "disabled" : ""}`}>
      <label htmlFor={`note-${todoId}`}>Note*</label>
      <input
        type="text"
        id={`note-${todoId}`}
        value={note}
        onChange={(e) => onNoteChange(e, todoId)}
        className="note-input"
        placeholder="Reason for unsatisfying criteria"
        required
        disabled={isDisabled} // Disable the input if confirmed
      />
    </form>
  );
};
