import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { EditTodoForm } from "./EditTodoForm";
import { NoteForm } from "./NoteForm";
import Button from "./Button";

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([
    { id: 1, task: "Engine Performance", completed: false, isEditing: false, showNoteForm: false, note: "" },
    { id: 2, task: "Tire Condition", completed: false, isEditing: false, showNoteForm: false, note: "" },
    { id: 3, task: "Brake Condition", completed: false, isEditing: false, showNoteForm: false, note: "" },
    { id: 4, task: "Headlights & Signals", completed: false, isEditing: false, showNoteForm: false, note: "" },
    { id: 5, task: "Windshields & Wipers", completed: false, isEditing: false, showNoteForm: false, note: "" }
  ]);

  const [carName, setCarName] = useState('');
  const [isCarAdded, setIsCarAdded] = useState(false); // State to check if car was added

  const toggleComplete = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id
          ? { ...todo, showNoteForm: !todo.showNoteForm, completed: false }
          : todo
      )
    );
  };

  const handleNoteChange = (e, id) => {
    const { value } = e.target;
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, note: value } : todo
      )
    );
  };

  const handleOutsideClick = (id) => {
    const todo = todos.find((t) => t.id === id);
    if (!todo.note.trim()) {
      alert("Note is required for unsatisfying criteria.");
    } else {
      setTodos(
        todos.map((todo) =>
          todo.id === id ? { ...todo, completed: true, showNoteForm: false } : todo
        )
      );
    }
  };

  const editTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const editTask = (task, id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const handleInspectionResult = () => {
    if (carName) {
      alert(`Inspection results for ${carName} submitted!`);
    } else {
      alert('Please provide a car name.');
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Car Inspection Report</h1>
      <TodoForm setCarName={setCarName} setIsCarAdded={setIsCarAdded} />

      {/* Conditionally show todos only if a car is added */}
      {isCarAdded && (
        <>
          {todos.map((todo) =>
            todo.isEditing ? (
              <EditTodoForm editTodo={editTask} task={todo} />
            ) : (
              <div key={todo.id}>
                <Todo
                  key={todo.id}
                  task={todo}
                  editTodo={editTodo}
                  toggleComplete={toggleComplete}
                />
                {/* Conditionally show the note form if the task is clicked */}
                {todo.showNoteForm && (
                  <NoteForm
                    note={todo.note}
                    onNoteChange={handleNoteChange}
                    todoId={todo.id}
                    onOutsideClick={handleOutsideClick} // Handle outside click to validate note
                  />
                )}
              </div>
            )
          )}
          <Button onClick={handleInspectionResult} className="inspection-result-btn">
            Inspection Result
          </Button>
        </>
      )}
    </div>
  );
};
