import React, { useState } from 'react';

export const TodoForm = ({ setCarName, setIsCarAdded }) => {
  const [value, setValue] = useState('');
  const [apiResponse, setApiResponse] = useState(''); 
  const [isCarAdded, setLocalCarAdded] = useState(false); 

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (value) {
      try {
        // POST request to the API
        const response = await fetch('http://localhost:8080/cars', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name: value }), 
        });

        const data = await response.json(); 

        if (!response.ok) {
          console.error('Error from API:', data.message || 'Unknown error');
          throw new Error('Car has already existed!');
        }

        setCarName(value); 
        setApiResponse('Car added successfully!'); 
        setIsCarAdded(true); 
        setLocalCarAdded(true); 
      } catch (error) {
        console.error('Caught Error:', error.message); 
        setApiResponse('Car has already existed!'); 
        setIsCarAdded(false); 
        setLocalCarAdded(false); 
      }

      // setValue(''); 
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="TodoForm">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          className="todo-input"
          placeholder="Car name"
          disabled={isCarAdded} 
        />
        {/* Conditionally render the button based on isCarAdded */}
        {!isCarAdded && (
          <button type="submit" className="todo-btn">
            Report Inspection
          </button>
        )}
      </form>

      {/* Conditionally render the API response directly under the form */}
      {apiResponse && (
        <p className="api-response">
          {apiResponse}
        </p>
      )}

      {/* Conditionally render the message if a car is added successfully */}
      {isCarAdded && (
        <p className="message">
          Please select all UNSATISFYING criteria
        </p>
      )}
    </div>
  );
};
