import React, { useRef, useEffect } from 'react';

export const NoteForm = ({ note, onNoteChange, todoId, onOutsideClick }) => {
  const noteInputRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (noteInputRef.current && !noteInputRef.current.contains(event.target)) {
        onOutsideClick(todoId); // Trigger the outside click handler passed from the parent
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onOutsideClick, todoId]);

  return (
    <form className="NoteForm">
      <label htmlFor={`note-${todoId}`}>Note*</label>
      <input
        type="text"
        id={`note-${todoId}`}
        value={note}
        onChange={(e) => onNoteChange(e, todoId)} // Capture input and update note
        className="note-input"
        placeholder="Reason for unsatisfying criteria"
        ref={noteInputRef}
        required
      />
    </form>
  );
};
