import React, { useState } from 'react';

export const TodoForm = ({ setCarName }) => {
  const [value, setValue] = useState('');
  const [showMessage, setShowMessage] = useState(false);
  const [apiResponse, setApiResponse] = useState(''); // To display API response message

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (value) {
      try {
        // POST request to the API
        const response = await fetch('http://localhost:8080/cars', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name: value }), // Send the car name as payload
        });

        if (!response.ok) {
          throw new Error('Failed to add car');
        }

        const data = await response.json();
        setCarName(value); // Update the car name state
        setApiResponse('Car added successfully'); // Display success message
      } catch (error) {
        console.error(error);
        setApiResponse('Error adding car'); // Display error message
      }

      setShowMessage(true); // Show message under the input field
      setValue(''); // Clear the input field after submission
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="TodoForm">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          className="todo-input"
          placeholder="Car name"
        />
        <button type="submit" className="todo-btn">Report Inspection</button>
      </form>

      {/* Conditionally render the message */}
      {showMessage && (
        <p className="message">
          Please select all unsatisfactory criteria
        </p>
      )}

      {/* Conditionally render API response */}
      {apiResponse && (
        <p className="api-response">
          {apiResponse}
        </p>
      )}
    </div>
  );
};
