import React, {useState} from 'react'

export const EditTodoForm = ({editTodo, task}) => {
    const [value, setValue] = useState(task.task);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault(); 
        setIsLoading(true); 
        setError(null); // Reset the error before making a new request

        try {
          // Make the PUT request to update the task description
          const response = await fetch(`http://localhost:8080/inspection-criteria/${task.id}/description`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ description: value }), // Send the updated description in the body
          });

          if (!response.ok) {
            throw new Error('Failed to update inspection criteria'); // Handle any non-success response
          }

          // If the API call is successful, update the task locally
          editTodo(value, task.id); // Update the task and exit the editing mode
        } catch (err) {
          console.error('Error:', err); // Log the error to the console
          setError('Failed to update inspection criteria. Please try again.'); // Show error to the user
        } finally {
          setIsLoading(false); // Stop loading
        }
      };

      return (
        <form onSubmit={handleSubmit} className="TodoForm">
          <input
            type="text"
            value={value}
            onChange={(e) => setValue(e.target.value)} // Update local state on user input
            className="todo-input"
            placeholder="Update criteria"
            disabled={isLoading} // Disable the input while the API call is being made
          />
          <button type="submit" className="todo-btn" disabled={isLoading}>
            {isLoading ? 'Updating...' : 'Update'}
          </button>
          {error && <p className="error-message">{error}</p>} {/* Display error message if there is an error */}
        </form>
      );
}
