import React, { useState } from 'react';

export const EditTodoForm = ({ editTodo, task }) => {
  const [value, setValue] = useState(task.task);
  const [isLoading, setIsLoading] = useState(false); // Track the loading state
  const [error, setError] = useState(''); // Track the error message

  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsLoading(true);
    setError(''); // Reset error message before submission

    try {
      // PUT request to the backend with the id and the updated description
      const response = await fetch(`http://localhost:8080/inspection-criteria/${task.id}/description`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ description: value }),
      });

      if (!response.ok) {
        throw new Error('Failed to update inspection criteria');
      }

      const updatedCriteria = await response.json();
      console.log('Inspection criteria updated:', updatedCriteria);

      // After API call is successful, call editTodo to update the local state
      editTodo(value, task.id);
    } catch (error) {
      console.error('Error updating inspection criteria:', error);
      setError('Failed to update the criteria. Please try again.'); // Set error message
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  return (
    <form onSubmit={handleSubmit} className="TodoForm">
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        className="todo-input"
        placeholder="Update criteria"
        disabled={isLoading} // Disable input while loading
      />
      <button type="submit" className="todo-btn" disabled={isLoading}>
        {isLoading ? 'Updating...' : 'Update'}
      </button>

      {/* Conditionally render error message */}
      {error && (
        <p className="error-message">
          {error}
        </p>
      )}
    </form>
  );
};
