import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { EditTodoForm } from "./EditTodoForm";
import { NoteForm } from "./NoteForm";
import Button from "./Button";
import { useNavigate } from "react-router-dom";

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([
    { id: 1, task: "Engine Performance", completed: false, isEditing: false, note: "", confirmed: false },
    { id: 2, task: "Tire Condition", completed: false, isEditing: false, note: "", confirmed: false },
    { id: 3, task: "Brake Condition", completed: false, isEditing: false, note: "", confirmed: false },
    { id: 4, task: "Headlights & Signals", completed: false, isEditing: false, note: "", confirmed: false },
    { id: 5, task: "Windshields & Wipers", completed: false, isEditing: false, note: "", confirmed: false }
  ]);

  const [carName, setCarName] = useState('');
  const [isCarAdded, setIsCarAdded] = useState(false);
  const navigate = useNavigate(); 


  // Toggle between complete and incomplete
  const toggleComplete = (id) => {
    setTodos(
      todos.map((todo) => {
        if (!todo.completed && todo.id === id) {
          return { ...todo, completed: true }; 
        }
        if (todo.completed && todo.id === id) {
          return { ...todo, completed: false, note: "" };
        }
        return todo;
      })
    );
  };

  const handleNoteChange = (e, id) => {
    const { value } = e.target;
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, note: value } : todo));
  };

  const editTodo = (id) => {
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo));
  };

  const editTask = (task, id) => {
    setTodos(todos.map((todo) => todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo));
  };

  // Confirm Todo (save button logic)
  const confirmTodo = async (id) => {
    const todo = todos.find((t) => t.id === id);

    const isGood = !todo.completed; // If not completed, isGood = true
    const note = todo.note; // Get the note

    try {
      // Make sure 'isGood' and 'note' are passed as query parameters
      const response = await fetch(`http://localhost:8080/car-inspections/${carName}/${id}?isGood=${isGood}&note=${encodeURIComponent(note)}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        const contentType = response.headers.get('content-type');

        // Check if the response is JSON
        if (contentType && contentType.includes('application/json')) {
          const errorData = await response.json();
          console.error('Error from API (JSON):', errorData);
          throw new Error(errorData.message || 'Failed to save car inspection.');
        } else {
          // Handle non-JSON responses (like plain text or HTML)
          const errorText = await response.text();
          console.error('Error from API (non-JSON):', errorText);
          throw new Error(errorText || 'Failed to save car inspection.');
        }
      }

      // If successful, confirm the todo
      setTodos(
        todos.map((todo) => 
          todo.id === id ? { ...todo, confirmed: true, isEditing: false } : todo
        )
      );

    } catch (error) {
      console.error('Error during POST request:', error.message);
      alert('Error in saving car inspection: ' + error.message);
    }
  };

  // Check if all criteria (todos) are confirmed
  const areAllCriteriaSaved = () => {
    return todos.every((todo) => todo.confirmed);
  };

  // Map the car status to a descriptive result
  const mapCarStatus = (status) => {
    if (status === 0) {
      return 'Not inspected yet';
    }
    if (status === 1) {
      return 'Inspecting';
    }
    if (status === 2) {
      return 'Inspected';
    }
  };

  // Fetch Car Inspection Result
  const handleInspectionResult = async () => {
    if (!areAllCriteriaSaved()) {
      alert('Please save all criteria before getting the car inspection result.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/cars/${carName}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error('Failed to get car inspection result: ' + errorText);
      }

      const carData = await response.json(); // Parse the JSON response
      const carStatus = mapCarStatus(carData.status); // Map car status

      // Show an alert with the car status
      alert(`Your car inspection result is: ${carStatus}`);
      navigate('/'); 

    } catch (error) {
      console.error('Error in fetching car inspection result:', error);
      alert('Error in fetching car inspection result: ' + error.message);
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Car Inspection Report</h1>
      <TodoForm setCarName={setCarName} setIsCarAdded={setIsCarAdded} />

      {isCarAdded && (
        <>
          {todos.map((todo) =>
            todo.isEditing ? (
              <EditTodoForm editTodo={editTask} task={todo} />
            ) : (
              <div key={todo.id}>
                <Todo
                  key={todo.id}
                  task={todo}
                  editTodo={editTodo}
                  toggleComplete={toggleComplete}
                  confirmTodo={confirmTodo} // Pass the confirm function to each todo
                  isConfirmDisabled={!todo.note.trim() && todo.completed} // Disable if note is empty for completed tasks
                />
                {/* Always show the note form if the task is completed */}
                {todo.completed && (
                  <NoteForm
                    note={todo.note}
                    onNoteChange={handleNoteChange}
                    todoId={todo.id}
                    isDisabled={todo.confirmed} // Disable if confirmed
                  />
                )}
              </div>
            )
          )}
          <Button onClick={handleInspectionResult} className="inspection-result-btn">
            Get Your Car Inspection Result
          </Button>
        </>
      )}
    </div>
  );
};
