import React, { useState } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { v4 as uuidv4 } from "uuid";
import { EditTodoForm } from "./EditTodoForm";

export const TodoWrapper = () => {
    const [todos, setTodos] = useState([
        { id: uuidv4(), task: "Engine Peformance", completed: false, isEditing: false },
        { id: uuidv4(), task: "Tire Condition", completed: false, isEditing: false },
        { id: uuidv4(), task: "Brake Condition", completed: false, isEditing: false },
        { id: uuidv4(), task: "Headlights & Signals", completed: false, isEditing: false },
        { id: uuidv4(), task: "Windshields & Wipers", completed: false, isEditing: false }
      ]);

  const addTodo = (todo) => {
    setTodos([
      ...todos,
      { id: uuidv4(), task: todo, completed: false, isEditing: false },
    ]);
  }

  const toggleComplete = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  }

  const editTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
  }

  const editTask = (task, id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, task, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const handleNoteChange = (e, id) => {
    const { value } = e.target;
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, note: value } : todo
    ));
  };

  return (
    <div className="TodoWrapper">
      <h1>Car Inspection Report</h1>
      <TodoForm addTodo={addTodo} />
      {/* display todos */}
      {todos.map((todo) =>
        todo.isEditing ? (
          <EditTodoForm editTodo={editTask} task={todo} />
        ) : (
          <Todo
            key={todo.id}
            task={todo}
            editTodo={editTodo}
            toggleComplete={toggleComplete}
          />
        )
      )}
      {todos.map((todo) =>
        todo.completed
    )}
    </div>
  );
};
