import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenToSquare } from '@fortawesome/free-solid-svg-icons'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import { Checkbox } from 'antd' // Import Checkbox from antd
import type { CheckboxProps } from 'antd'

export const Todo = ({task, deleteTodo, editTodo}) => {
    const onDoneChange: CheckboxProps['onChange'] = (e) => {
        console.log(`Done checked = ${e.target.checked}`);
    }

    const onNotDoneChange: CheckboxProps['onChange'] = (e) => {
        console.log(`Not Done checked = ${e.target.checked}`);
    }

  return (
    <div className="Todo">
        <p className={`${task.completed ? "completed" : "incompleted"}`}>{task.task}</p>
        <div className="checkboxes">
            <Checkbox onChange={onDoneChange}>Done</Checkbox>
            <Checkbox onChange={onNotDoneChange}>Not Done</Checkbox>

        <FontAwesomeIcon className="edit-icon" icon={faPenToSquare} onClick={() => editTodo(task.id)} />
        <FontAwesomeIcon className="delete-icon" icon={faTrash} onClick={() => deleteTodo(task.id)} />
        </div>
    </div>
  )
}
